//Parses a CIDR Range into beginning and ending IPv4 Addresses
//For example: '10.0.0.0/24'
//Returns     ['10.0.0.0', '10.0.0.255']
module.exports = function (CIDR, vType = "v4") {

	if (vType === "v4") {
		var Address4 = require('ip-address').Address4;
		var addr4 = new Address4(CIDR);
		return [addr4.parsedAddress.join('.'), addr4.endAddress().address];
	}

	if (vType === "v6") {
		var Address6 = require('ip-address').Address6;
		var addr6 = new Address6(CIDR);
		return [addr6.parsedAddress.join(':'), addr6.endAddress().address];
	}
	
	return ["0.0.0.0", "0.0.0.0"];
}