// chuyển đổi một đối tượng json sang mệnh đề wheres= sau dấu ? để đưa vào GET
module.exports = (jsonWhere) => {
    let strWhere = "";
    if (!jsonWhere || typeof jsonWhere !== "object") {
        return "";
    }
    try {
        strWhere = JSON.stringify(jsonWhere, (key, value) => {
            if (value && typeof value === "string") {
                return value.replace(/\s/g, "^");
            }
            return value;
        }) || "";

        strWhere = strWhere.replace(/"/g, "");
    }
    catch { }


    return strWhere;
}