const mongoJsonWhere2Sql = require("./mongo-json-where-2-sql");
module.exports = (wheresDao, isAll = true) => {
    if (!wheresDao || !Array.isArray(wheresDao) || !wheresDao.length) {
        return "";
    }
    let whereSql = "";
    for (let col of wheresDao) {
        let jsonAndOne = {};
        jsonAndOne[col.name] = col.value;
        whereSql += `${whereSql ? ` AND ` : ``}` + mongoJsonWhere2Sql(jsonAndOne, false);
    }
    return whereSql ? `${isAll ? ` WHERE ` : ``}${whereSql}` : "";
}
